function containsInterwiki(e,t){for(var n=0;n<e.length;n++){if(e[n].className.indexOf("interwiki-"+t)!=-1)return n}return-1}function fixInterwikiOrder(){var e=document.getElementById("p-lang").getElementsByTagName("LI");var t=interwikiOrder[interwikiOrder.length-1]=="slutt"?2:1;for(var n=interwikiOrder.length-t;n>-1;n--){var r=containsInterwiki(e,interwikiOrder[n]);if(r!=-1){var i=document.getElementById("p-lang").getElementsByTagName("UL")[0];i.insertBefore(e[r],i.firstChild)}}if(t==2)while(e.length!=interwikiOrder.length-1)i.removeChild(i.lastChild)}function initInterwikiOrder(){if(!document.getElementById("p-lang"))return;var e=sajax_init_object();e.open("GET",wgServer+wgScriptPath+"/index.php?title=User:"+encodeURIComponent(wgUserName)+"/لغاتي&action=raw",true);e.onreadystatechange=function(){if(e.readyState!=4)return;var t=e.responseText.split("\n");var n=new RegExp("\\*\\s?([^$]*)");for(var r=0;r<t.length;r++){var i=n.exec(t[r]);if(i)interwikiOrder.push(i[1]);else break}if(interwikiOrder[0])fixInterwikiOrder()};e.send(null)}mediaWiki.config.set("wgLanglinks","no-sort no-reorder");var interwikiOrder=new Array;addOnloadHook(initInterwikiOrder)